import React, { useState, useEffect } from "react";
import { MenuItem, FormControl, Select } from "@material-ui/core";
import "./App.css";
import InfoBox from "./infoBox";
//NOTE: UseEffects:- Runs a piece of code only under some given condition.
// It will run as soon as the component loads for the first time and only once this happens.

function App() {
  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState("Worldwide");

  useEffect(() => {
    const getCountriesData = async () => {
      // https://disease.sh/v3/covid-19/countries
      await fetch("https://disease.sh/v3/covid-19/countries")
        .then((response) => response.json())
        .then((data) => {
          const countries = data.map((country) => ({
            name: country.country,
            value: country.countryInfo.iso2,
          }));
          setCountries(countries);
        });
    };
    getCountriesData();
  }, []);

  const onCountrychange = async (event) => {
    const countryCode = event.target.value;
    console.log(countryCode);
    setCountry(countryCode);
  };

  return (
    <div className="app">
      <div className="app__header">
        <h1>Covid 19 Tracker</h1>
        <FormControl className="app__dropdown">
          <Select
            className="appheader__dropdown"
            variant="outlined"
            value={country}
            onChange={onCountrychange}
          >
            <MenuItem value="Worldwide">WorldWide</MenuItem>
            {countries.map((country) => (
              <MenuItem value={country.value}>{country.name}</MenuItem>
            ))}
          </Select>
        </FormControl>
      </div>

      {/*Header*/}
      {/* Title  */}
      <div className="app_stats">
        <InfoBox title="Corona Cases" cases={2000} total={5000}></InfoBox>
        <InfoBox title="Recovered" cases={2000} total={5000}></InfoBox>
        <InfoBox title="Deaths" cases={2000} total={5000}></InfoBox>
        {/* infoboxes */}
        {/* infoboxes */}
      </div>

      {/* Tables */}
      {/* Graphs */}
      {/* Maps  */}
    </div>
  );
}

export default App;
