import { Circle } from "leaflet";

export const sortData = (data) => {
  let sortedData = [...data];
  sortedData.sort((a, b) => {
    if (a.cases > b.cases) {
      return -1;
    } else {
      return 1;
    }
  });
  return sortedData;
};

export const showDataMap = (data, caseType = "cases") =>(
  //   const colors = "#FFD600";

  data.map(country => (
    <Circle
      center={[country.countryInfo.lat, countryInfo.long]}
      fillOPacity={0.5}
      color="#5BFF62"
      fillColor="#FFD600"
      radius={Math.sqrt(country[caseType]) * colors.multiplier}
    ></Circle>
  )));
